function callBackMail(){$(".callback-mail-button").click(function(){var n=$("#callBackName").val(),t=$("#callBackPhone").val(),r=$("#callBackMisc").val(),i;if(n===""||t===""){alertify.error("Введите Имя и телефон!");return}i={Name:n,Phone:t,Misc:r};$.ajax({type:"POST",url:"/Mail/CallBackMail",data:i,success:function(n){$("#callBackMail").html(n);alertify.success("Сообщение отправлено. Скоро мы Вам перезвоним.")},error:function(n){console.log(n)}})})}$(".carousel").carousel({interval:5e3});$(function(){$(document).ready(function(){callBackMail()})});