"use strict";function orderMail(){$(".order-mail-button").click(function(){var i=$("#orderName").val(),r=$("#orderPhone").val(),n,t;if(i===""||r===""){alertify.error("Введите Имя и телефон!");return}n=$("#orderForm");t=n.serialize();$.ajax({type:"POST",url:"/Mail/OrderMail",data:t,success:function(n){$("#orderForm").html(n);alertify.success("Ваш заказ отправлен. Скоро мы Вам перезвоним.")},error:function(n){console.log(n)}})})}function callBackMail(){$(".callback-mail-button").click(function(){var n=$("#callBackName").val(),t=$("#callBackPhone").val(),r=$("#callBackMisc").val(),i;if(n===""||t===""){alertify.error("Введите Имя и телефон!");return}i={Name:n,Phone:t,Misc:r};$.ajax({type:"POST",url:"/Mail/CallBackMail",data:i,success:function(n){$("#callBackMail").html(n);alertify.success("Сообщение отправлено. Скоро мы Вам перезвоним.")},error:function(n){console.log(n)}})})}$(function(){$(document).ready(function(){$(".carousel.lazy").on("slide.bs.carousel",function(n){var t;t=$(n.relatedTarget).find("img[data-src]");t.attr("src",t.data("src"));t.removeAttr("data-src")});$(".carousel").carousel({interval:5e3});callBackMail();orderMail()})});